import LoginForm from './LoginForm';
import LoggedIn from './LoggedIn';


function Login (props) {

    const [user, setUser] = useState(null);

    /*function fetchUserHandler() {
        const response = fetch('http://127.0.0.1:5000/login');
        const data = response.json();
        console.log(data);
        setUser(data.user);

    }*/

    function fetchUserHandler() {
      fetch('http://127.0.0.1:5000/login')
      .then((response) => {
        return response.json(); //Si aquí hay un error, quiere decir que el back-end está mal.
      })
      .then((data) => { //Lambda en JS
        setUser(data.user);
      })
  };

  useEffect(() => {
      fetchUserHandler();
      console.console.log(user);
  }, []);

    return (
        <div>
            { user }
            {user ? <LoggedIn user={user}/> : <LoginForm setUser={setUser}/>}
        </div>
    );
}

export default Login;
