import React, { useState } from 'react';
import './CRUDUsuarios.css';

const CRUDUsuarios = (props) => {

    const [nuevoUsuaio, setNuevoUsuario] = useState({ nombre: '', apPat: '', apMat: '', password: '', email: '', profilePicture: '', superUser: '' });

    const eliminarUsuario = (id) => {
        const nuevosUsuarios = [...props.usuarios];
        nuevosUsuarios.splice(id, 1);
        props.setUsuarios(nuevosUsuarios);
    };

    return (
        <div>
            <h2>Usuarios</h2>
            <ul>
                {props.usuarios.map(usuario => (
                    <li key={usuario.id}>
                        {usuario.nombre} {usuario.apellido}
                        <button onClick={() => eliminarUsuario(usuario.id)}>Eliminar</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default CRUDUsuarios;
