import React, { useState } from 'react';
import './CRUDUsuarios.css';

const CRUDUsuarios = () => {
    const [usuarios, setUsuarios] = useState([
        { idUsuario: 1, nombre: 'Gael', apPat: 'Garcia', apMat: 'Aguilera', password: '1234', email: 'gaelgaag10@ciencias.unam.mx', profilePicture: null, superUser: true },
        { idUsuario: 2, nombre: 'Ulises', apPat: 'Garcia', apMat: 'Aguilera', password: '1234', email: 'uga@hotmail.com', profilePicture: null, superUser: true }
    ]);

    const [nuevoUsuario, setNuevoUsuario] = useState({ nombre: '', apPat: '', apMat: '', password: '', email: '', profilePicture: '', superUser: '' });

    const handleInputChange = (e) => {
        const { nombre, valor } = e.target;
        setNuevoUsuario({ ...nuevoUsuario, [nombre]: valor});
    };

    const agregarUsuario = () => {
        const id = usuarios.length + 1;
        setUsuarios([...usuarios, { id, ...nuevoUsuario}]);
        setNuevoUsuario({ nombre: '', apPat: '', apMat: '', password: '', email: '', profilePicture: '', superUser: '' });
    };

    const eliminarUsuario = (id) => {
        const nuevosUsuarios = usuarios.filter(usuario => usuario.idUsuario != id);
        setUsuarios(nuevosUsuarios);
    };

    return (
        <div>
            <h2>Usuarios</h2>
            <ul>
                {usuarios.map(usuario => (
                    <li key={usuario.id}>
                        {usuario.nombre} {usuario.apellido} - Edad: {usuario.edad}
                        <button onClick={() => eliminarUsuario(usuario.id)}>Eliminar</button>
                    </li>
                ))}
            </ul>
            <h3>Agregar Nuevo Usuario</h3>
            <input type="text" name="nombre" placeholder="Nombre" value={nuevoUsuario.nombre} onChange={handleInputChange} />
            <input type="text" name="apellidoPaterno" placeholder="Apellido Paterno" value={nuevoUsuario.apPat} onChange={handleInputChange} />
            <input type="text" name="apellidoMaterno" placeholder="Apellido Materno" value={nuevoUsuario.apMat} onChange={handleInputChange} />
            <input type="password" name="password" placeholder="ContraseÃ±a" value={nuevoUsuario.password} onChange={handleInputChange} />
            <input type="email" name="email" placeholder="Email" value={nuevoUsuario.email} onChange={handleInputChange} />
            <input type="text" name="foto" placeholder="Foto" value={nuevoUsuario.profilePicture} onChange={handleInputChange} />
            <input type="checkbox" name="edad" placeholder="SuperUser" value={nuevoUsuario.superUser} onChange={handleInputChange} />
            <button onClick={agregarUsuario}>Agregar Usuario</button>
        </div>
    );
};

export default CRUDUsuarios;
