import React, { useState } from "react";

import "./PeliculaForm.css";

const PeliculaFormAgregar = (props) => {
    const [nombre, setNombre] = useState("");
    const [genero, setGenero] = useState("");
    const [duracion, setDuracion] = useState(0);
    const [inventario, setInventario] = useState(0);

    const setNombreHandler = (event) => {
        setNombre(event.target.value);
    };

    const setGeneroHandler = (event) => {
        setGenero(event.target.value);
    };

    const setDuracionHandler = (event) => {
        setDuracion(event.target.value);
    };

    const setInventarioHandler = (event) => {
        setInventario(event.target.value);
    };

    const submitHandler = (event) => {
        event.preventDefault();

        const pelicula = {
            nombre: nombre,
            genero: genero,
            duracion: duracion,
            inventario: inventario,
        };

        if (
            nombre === "" ||
            inventario === ""
        ) {
            alert("Campos vacíos!!");
            return;
        }

        props.guardarPelicula(pelicula);
        setNombre("");
        setGenero("");
        setDuracion(0);
        setInventario(0);
    };

    return (
        <form onSubmit={submitHandler}>
        <label>Nombre: </label>
        <input type="text" value={nombre} onChange={setNombreHandler}/>
        <a class="red-text"> *</a>
        <br />

        <label>Género: </label>
        <input type="text" value={genero} onChange={setGeneroHandler}/>
        <br />

        <label>Duración: </label>
        <input type="number" value={duracion} onChange={setDuracionHandler} />
        <br />

        <label>Inventario: </label>
        <input type="number" value={inventario} onChange={setInventarioHandler} />
        <a class="red-text"> *</a>
        <br />

        <button className="button" type="submit">Agregar película</button>

        </form>
        );
    };

export default PeliculaFormAgregar;
