import React, { useState } from "react";

import "./UsuarioForm.css";

const UsuarioForm = (props) => {
  const [nombre, setNombre] = useState("");
  const [apPat, setApPat] = useState("");
  const [apMat, setApMat] = useState("");

  const setNombreHandler = (event) => {
    setNombre(event.target.value);
  };

  const setApPatHandler = (event) => {
    setApPat(event.target.value);
  };

  const setApMatHandler = (event) => {
    setApMat(event.target.value);
  };

  const guardarUsuario = (usuario) => {
      const nuevosUsuarios = [usuario, ...props.usuarios];
      props.setUsuarios(nuevosUsuarios);
  }

  const submitHandler = (event) => {
    event.preventDefault();

    const usuario = {
      nombre: nombre,
      apPat: apPat,
      apMat: apMat,
    };
    if (
      nombre === "" ||
      apPat === "" ||
      apMat === ""
    ) {
      alert("Campos vacíos!!");
      return;
    }
    guardarUsuario(usuario);
    setNombre("");
    setApPat("");
    setApMat("");
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="nuevo-usuario__controls">
        <div className="nuevo-usuario__control">
          <label>Nombre: </label>
          <input
            type="text"
            value={nombre}
            onChange={setNombreHandler}
          />
        </div>
        <div className="nuevo-usuario__control">
          <label>Apellido: </label>
          <input
            type="text"
            value={apPat}
            onChange={setApPatHandler}
          />
        </div>
        <div className="nuevo-usuario__control">
          <label>Num. Cta.: </label>
          <input
            type="text"
            value={apMat}
            onChange={setApMatHandler}
          />
        </div>
        <div className="nuevo-usuario__actions">
          <button type="submit">Agregar usuario</button>
        </div>
      </div>
    </form>
  );
};

export default UsuarioForm;
