import React, { useState } from 'react';
import './CRUDUsuarios.css';

import UsuarioForm from "./UsuarioForm/UsuarioForm";

const CRUDUsuarios = (props) => {

    const [nuevoUsuario, setNuevoUsuario] = useState({ nombre: '', apPat: '', apMat: '', password: '', email: '', profilePicture: '', superUser: '' });

    const eliminarUsuario = (id) => {
        const nuevosUsuarios = [...props.usuarios];
        nuevosUsuarios.splice(id, 1);
        props.setUsuarios(nuevosUsuarios);
    };

    return (
        <div>
            <h2>Usuarios</h2>

            <ul>
                {props.usuarios.map(usuario => (
                    <li key={usuario.id}>
                        {usuario.nombre} {usuario.apellido}
                        <button onClick={() => eliminarUsuario(usuario.id)}>Eliminar</button>
                    </li>
                ))}
            </ul>

            <h3>Agregar Nuevo Usuario</h3>
            <UsuarioForm usuarios={props.usuarios} setUsuarios={props.setUsuarios}/>
        </div>
    );
};

export default CRUDUsuarios;
