import React, { useState } from "react";

import "./UsuarioForm.css";

const UsuarioForm = (props) => {
  const [nombre, setNombre] = useState("");
  const [apPat, setApPat] = useState("");
  const [apMat, setApMat] = useState("");

  const setNombreHandler = (event) => {
    setNombre(event.target.value);
  };

  const setApPatHandler = (event) => {
    setApPat(event.target.value);
  };

  const setApMatHandler = (event) => {
    setApMat(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const usuario = {
      nombre: nombre,
      apPat: apPat,
      apMat: apMat,
    };
    if (
      nombre === "" ||
      apPat === "" ||
      apMat === ""
    ) {
      alert("Campos vacíos!!");
      return;
    }
    props.onGuardarAlumno(alumno);
    setNombreIngresado("");
    setApellidoIngresado("");
    setNumCtaIngresado("");
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="nuevo-alumno__controls">
        <div className="nuevo-alumno__control">
          <label>Nombre: </label>
          <input
            type="text"
            value={nombreIngresado}
            onChange={cambioNombreHandler}
          />
        </div>
        <div className="nuevo-alumno__control">
          <label>Apellido: </label>
          <input
            type="text"
            value={apellidoIngresado}
            onChange={cambioApellidoHandler}
          />
        </div>
        <div className="nuevo-alumno__control">
          <label>Num. Cta.: </label>
          <input
            type="text"
            value={numCtaIngresado}
            onChange={cambioNumCtaHandler}
          />
        </div>
        <div className="nuevo-alumno__actions">
          <button type="submit">Agregar alumno</button>
        </div>
      </div>
    </form>
  );
};

export default AlumnoForm;
