import React, { useState } from 'react';
import './CRUDUsuarios.css';

import UsuarioForm from "./UsuarioForm/UsuarioForm";

const CRUDUsuarios = (props) => {

    const guardarUsuario = (usuario) => {
        const ultimoUsuario = props.usuarios[prop.usuarios.lenght - 1];
        const ultimoID = ultimoUsuario.idUsuario;
        usuario.idUsuario = ultimoID + 1;
        const nuevosUsuarios = [...props.usuarios, usuario];
        props.setUsuarios(nuevosUsuarios);
    }

    const eliminarUsuario = (id) => {
        const nuevosUsuarios = [...props.usuarios];
        const indice = nuevosUsuarios.findIndex(usuario => usuario.idUsuario === id);
        nuevosUsuarios.splice(indice, 1);
        props.setUsuarios(nuevosUsuarios);
    }

    return (
        <div>
            <h2>Usuarios</h2>

            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Apellido Paterno</th>
                        <th>Apellido Materno</th>
                        <th>Eliminar</th>
                    </tr>
                </thead>
                <tbody>
                    {props.usuarios.map(usuario => (
                        <tr key={usuario.idUsuario}>
                            <td>{usuario.idUsuario}</td>
                            <td>{usuario.nombre}</td>
                            <td>{usuario.apPat}</td>
                            <td>{usuario.apMat}</td>
                            <td><button onClick={() => eliminarUsuario(usuario.idUsuario)}>Eliminar</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h3>Agregar Nuevo Usuario</h3>
            <UsuarioForm guardarUsuario={guardarUsuario}/>
        </div>
    );
};

export default CRUDUsuarios;
