import React, { useState } from "react";

import "./RentaForm.css";

const RentaFormActualizar = (props) => {
    const [idRentar, setIdRentar] = useState(0);
    const [idUsuario, setIdUsuario] = useState(0);
    const [idPelicula, setIdPelicula] = useState(0);
    const [fechaRenta, setFechaRenta] = useState("");
    const [diasRenta, setDiasRenta] = useState(0);
    const [estatus, setEstatus] = useState(false);

    const setIdRentarHandler = (event) => {
        setIdRentar(event.target.value);
    };

    const buscarHandler = (event) => {
        event.preventDefault();

        setIdRentar(event.target.value);
        const indice = props.rentas.findIndex(renta => renta.idRentar === parseInt(idUsuario));
        const rent = props.rentas[indice];
        if (!rent){
            alert("ID no encontrado");
            setIdRentar(0);
            setIdUsuario(0);
            setIdPelicula(0);
            setFechaRenta("");
            setDiasRenta(0);
            setEstatus(false);
            return;
        }
        setIdRentar(rent.idUsuario);
        setNombre(rent.nombre);
        setApPat(rent.apPat);
        setApMat(rent.apMat);
        setPassword(rent.password);
        setEmail(rent.email);
        setSuperUser(rent.superUser);
    }

    const setIdUsuarioHandler = (event) => {
        setIdUsuario(event.target.value);
    };

    const setIdPeliculaHandler = (event) => {
        setIdPelicula(event.target.value);
    };

    const setFechaRentaHandler = (event) => {
        setFechaRenta(event.target.value);
    };

    const setDiasRentaHandler = (event) => {
        setDiasRenta(event.target.value);
    };

    const setEstatusHandler = (event) => {
        setEstatus(event.target.checked);
    };

    const submitHandler = (event) => {
        event.preventDefault();

        const renta = {
            idRentar: idRentar,
            idUsuario: idUsuario,
            idPelicula: idPelicula,
            fechaRenta: fechaRenta,
            diasRenta: diasRenta,
            estatus: estatus,
        };

        if (
            false
        ) {
            alert("Campos vacíos!!");
            return;
        }

        props.actualizarRenta(renta);
        setIdRentar(0);
        setIdUsuario(0);
        setIdPelicula(0);
        setFechaRenta("");
        setDiasRenta(0);
        setEstatus(false);
    };

    return (
        <form onSubmit={submitHandler}>

        <label>ID: </label>
        <input type="number" value={idUsuario} min="0" onChange={setIdUsuarioHandler}/>
        <button className="button" onClick={buscarHandler}>Buscar</button>
        <br />

        <label>ID Usuario: </label>
        <input type="number" value={idUsuario} onChange={setIdUsuarioHandler}/>
        <br />

        <label>ID Película: </label>
        <input type="number" value={idPelicula} onChange={setIdPeliculaHandler}/>
        <br />

        <label>Fecha Renta: </label>
        <input type="date" value={fechaRenta} onChange={setFechaRentaHandler} />
        <br />

        <label>Días de Renta: </label>
        <input type="number" value={diasRenta} onChange={setDiasRentaHandler} />
        <br />

        <label>Estatus: </label>
        <input type="checkbox" checked={estatus} onChange={setEstatusHandler} />
        <br />

        <button className="button" type="submit">Actualizar Renta</button>

        </form>
        );
    };

export default RentaFormActualizar;
