import React, { useState } from "react";

import "./UsuarioFormAgregar.css";

const UsuarioFormAgregar = (props) => {
  const [nombre, setNombre] = useState("");
  const [apPat, setApPat] = useState("");
  const [apMat, setApMat] = useState("");

  const setNombreHandler = (event) => {
    setNombre(event.target.value);
  };

  const setApPatHandler = (event) => {
    setApPat(event.target.value);
  };

  const setApMatHandler = (event) => {
    setApMat(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const usuario = {
      nombre: nombre,
      apPat: apPat,
      apMat: apMat,
    };
    if (
      nombre === "" ||
      apPat === "" ||
      apMat === ""
    ) {
      alert("Campos vacíos!!");
      return;
    }
    props.guardarUsuario(usuario);
    setNombre("");
    setApPat("");
    setApMat("");
  };

  return (
    <form onSubmit={submitHandler}>
          <label>Nombre: </label>
          <input
            type="text"
            value={nombre}
            onChange={setNombreHandler}
          />
          <br />
          <label>Apellido Paterno: </label>
          <input
            type="text"
            value={apPat}
            onChange={setApPatHandler}
          />
          <label>Apellido Materno: </label>
          <input
            type="text"
            value={apMat}
            onChange={setApMatHandler}
          />
          <button type="submit">Agregar usuario</button>
    </form>
  );
};

export default UsuarioFormAgregar;
